<style>
body{

margin: 0;
padding: 0;
font-family: Perpetua;
background: white;
background-image: url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSIgdbhUfGUnLmmCnGV8MGq89Hmp9dXOzOSTQ&usqp=CAU");
background-repeat: no-repeat;
background-attachment: fixed;
background-size: 100% 100%;
}
.items{
position: relative;
top: 50%;
left: 50%;
transform: translate(-50%, -50%);
width: 600px;
padding: 10px;
box-sizing: border-box;
bacground: white;
border-radius: 4px;
box-shadow: 0 5px 15px rgba(0,0,0,.5);
display: flex;
}
.items .images{
width: 150px;
flex: 0 0 150px;
border-radius: 4px;
 }
 
.items .images img
{
max-width: 100%;
border-radius: 5px;
}
.items .images img:hover{
transform: scale(2,2);
transition: .3s transform;
}
.items .description
{
padding-left: 20px;
}
.items .description b
{
margin: 0;
padding-left:0;
color: #000000;
font-size: 22px;
}

.items .description item-select, buynow-btn
{
margin: 0;
padding: 10px 0 0;
text-align: justify;
}
@media(max-width: 768px)
{
.items 
{
flex-direction: row;
max-width: 350px;
}
}
</style>
